mapper.update:
UPDATE vehicle_info
SET
car_number = #{car_number}, vehicle = #{vehicle}, company = #{company}, owner = #{owner}, bus = #{bus}, brand = #{brand}, name = #{name}, grade = #{grade}, fuel = #{fuel}, num = #{num}, color = #{color}, regist = NOW(), expire = NOW(), price = 1, special = #{special}, reg_d = NOW(), reg_add = #{reg_add}, insu_d = NOW(), insu_add = #{insu_add}, juk_d = #{juk_d}, juk_add = #{juk_add}, img1 = #{img1}, img2 = #{img2}, img3 = #{img3}, trash = 1 WHERE car_number = #{car_number};
